import axios from "axios";

const GOOGLE_API_KEY = "AIzaSyAnbbstU-FTLUf79odSuvJWWfTfHTA_ONM";

export function getMapPreview(lat, lng) {
  const imageURL = `https://maps.googleapis.com/maps/api/staticmap?center=${lat},${lng}&zoom=14&size=400x200&maptype=roadmap&markers=color:red%7Clabel:S%7C${lat},${lng}&key=${GOOGLE_API_KEY}`;

  // const imageURL = `https://maps.googleapis.com/maps/api/staticmap?center=${lat},${lng}&zoom=13&size=400x200&maptype=roadmap
  //   &markers=color:red%7Clabel:S%7C${lat},${lng}
  //   &key=${GOOGLE_API_KEY}&signature=YOUR_SIGNATURE`;

  return imageURL;
}

export async function getAddress(lat, lng) {
  const data = await axios.get(
    `https://maps.googleapis.com/maps/api/geocode/json?latlng=40.714224,-73.961452&key=${GOOGLE_API_KEY}`
  );
}
